import React, { useState } from 'react'
import './Panel.css'
import './WorkoutStart.css'
import SearchBar from './SearchBar';

const WorkoutStart = () => {
  const [search, setSearch] = useState(true);
  const [results, setResults] = useState([]);
  const [exercises, setExercises] = useState([]);

  const handleAdding = (e) =>{
    e.stopPropagation();
    const itemToAdd = e.target.value;
    setExercises([...exercises,itemToAdd]);
    console.log(exercises);
  }


  return (
    <div className='panel'>
        <div className='top'>
            <h1>Workout</h1>
        </div>
        <div className={search ? 'bottomWorkout hidden' : 'bottomWorkout'}>
            <button>Add an exercise</button>
        </div>
        <div className={search ? 'searchExercises' : 'searchExercises hidden'}>
          <SearchBar setResults={setResults}/>
          <ul className='searchExercisesList'>
            {results?.map((result,id) =>{
              return <li key={id} value={result} onClick={(event) =>{handleAdding(event)}}>
                {result.exercise}
              </li>
            })}
          </ul>
        </div>
    </div>
  )
}

export default WorkoutStart
