import React, { useEffect, useState } from 'react';
import Cookies from 'js-cookie';
import {LineChart, Line, ResponsiveContainer, XAxis, YAxis, Tooltip, Legend} from 'recharts';

const ProgressChart = ({exerciseId}) => {

    const [stats, setStats] = useState([]);
    const [normalizedData, setNormalizedData] = useState([]);


    const fetchExerciseStats = async (exerciseId) =>{
        try {
            const token = Cookies.get("accessToken");
            const userId = Cookies.get("userId");
            const response = await fetch(`http://localhost:3000/exercise/${exerciseId}/${userId}/workout-stats`, {
              method: "GET",
              headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${token}`,
              },
            });
            if (response.status === 200) {
              const result = await response.json();
              console.log(result);
              setStats(result);
              const totalValues = result.reduce((total, item) => total + item.orm + item.volume, 0);

              const normalizedData = result.map(item => ({
                ...item,
                ormPercentage: (item.orm / totalValues) * 100,
                volumePercentage: (item.volume / totalValues) * 100,
              }));
      
              setNormalizedData(normalizedData);
            }
          } catch (err) {
            console.log(err);
          }
    }

    useEffect(()=>{
        setStats([]);
        fetchExerciseStats(exerciseId);
    },[exerciseId]);

  return (
    <ResponsiveContainer width={300} height={250}>
        <LineChart width={250} height={250} data={normalizedData} >
            <XAxis  stroke='#fff'/>
            <YAxis domain={[0,100]} stroke='#fff' tickFormatter={(tick) => `${tick.toFixed(0)}%`}/>
            <Tooltip />
            <Line type="monotone" dataKey='ormPercentage' stroke='#2563eb'/>
            <Line type="monotone" dataKey='volumePercentage' stroke='#7c3aed'/>
        </LineChart>
    </ResponsiveContainer>
  )
}

export default ProgressChart
