import './App.css';
import Cookies from 'react-cookie'
import NavBar from './components/NavBar';
import Panel from './components/Panel';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import {
  createBrowserRouter,
  RouterProvider,
  Route,
  createRoutesFromElements,
  redirect
} from "react-router-dom";

function App() {

  function checkAuth() {
    const token = Cookies.get('accessToken');
    if (!token) {
      throw redirect('http://localhost:3001/login');
    }
    return null;
  }

  function checkNotAuth() {
    const token = Cookies.get('accessToken');
    if (token) {
      throw redirect('http://localhost:3001/')
    }
    return null;

  }


  const router = createBrowserRouter(
    createRoutesFromElements(
      <Route>
        <Route path='' element={<Panel />} loader={checkAuth}/>
        <Route path='/login' element={<LoginPage />} loader={checkNotAuth} />
        <Route path='/register' element={<RegisterPage />} loader={checkNotAuth} />
      </Route>
    )
  )


  return (
    <div className="App">
      <RouterProvider router={router} />

    </div>
  );
}

export default App;
