  return ( 
    <div className='panel'>
      <div className='top'>
        <h1>Welcome!</h1>
      </div>
      <div className='bottom'>
        <div className='container'>
          <ul>
            <h2>Your last workouts:</h2>
            <li>Friday</li>
            <li>Thursday</li>
            <li>Tuesday</li>
            <li>Monday</li>
            <button>Start a workout</button>
          </ul>

        </div>
      </div>
    </div>
  )
}

export default Panel
