import React, { useState } from 'react'

const Exercises = () => {
  const [results,setResults] = useState([]);

  const fetchExerciseData = async (id) =>{
    try {
      const token = Cookies.get("accessToken");
      const response = await fetch(`http://localhost:3000/exercise/${id}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });
      if (response.status === 200) {
        const result = await response.json();
        console.log(result);
        setExercisesInfo(result);
      }
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() =>{
    fetchExerciseData(exerciseId);
  },[exerciseId]);

  return (
    <div class='panel'>
      <div className={'searchExercises'}>
          <SearchBar setResults={setResults} />
          <ul className='searchExercisesList'>
            {
              results.length===0 ? (
                <p>No exercise found</p>
              ) : (
              results.map((result, id) => (
                <li key={id} onClick={() => handleSwitchingExercise(result)}>
                  {result.exercise}
                </li>
            )))}
          </ul>
        </div>
    </div>
  )
}

export default Exercises
