import React, { useEffect, useState } from 'react';
import Cookies from 'js-cookie';
import {LineChart, Line, ResponsiveContainer, XAxis, YAxis, Tooltip, Legend} from 'recharts';

const ProgressChart = ({exerciseId}) => {

    const [stats, setStats] = useState([]);
    const [normalizedData, setNormalizedData] = useState([]);


    const fetchExerciseStats = async (exerciseId) =>{
        try {
            const token = Cookies.get("accessToken");
            const userId = Cookies.get("userId");
            const response = await fetch(`http://localhost:3000/exercise/${exerciseId}/${userId}/workout-stats`, {
              method: "GET",
              headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${token}`,
              },
            });
            if (response.status === 200) {
              const result = await response.json();
              console.log(result);
              setStats(result);
              const totalVolume = result.reduce((total, item) => total + item.volume, 0);
              const totalOrm = result.reduce((total, item) => total + item.orm, 0);
      
              const normalizedData = result.map(item => ({
                ...item,
                volumePercentage: (item.volume / totalVolume) * 100,
                ormPercentage: (item.orm / totalOrm) * 100,
              }));
      
              setStats(normalizedData);
            }
          } catch (err) {
            console.log(err);
          }
    }

    useEffect(()=>{
        setStats([]);
        fetchExerciseStats(exerciseId);
    },[exerciseId]);

  return (
    <ResponsiveContainer width={300} height={250}>
      <LineChart width={250} height={250} data={stats}>
        <XAxis stroke="#fff" />
        <YAxis stroke="#fff" domain={[0, 'dataMax']} />
        <Tooltip 
          content={({ payload }) => {
            if (payload && payload.length) {
              const data = payload[0];
              return (
                <div style={{ backgroundColor: '#fff', padding: '10px' }}>
                  <p>Workout: {data.name}</p>
                  <p>Reps: {data.reps}</p>
                  <p>Weight: {data.weight} kg</p>
                  <p>ORM: {data.orm} kg (1RM)</p>
                  <p>Volume: {data.volume} kg</p>
                </div>
              );
            }
            return null;
          }}
        />
        <Line type="monotone" dataKey="ormPercentage" stroke="#2563eb" />
        <Line type="monotone" dataKey="volumePercentage" stroke="#7c3aed" />
      </LineChart>
    </ResponsiveContainer>
  )
}

export default ProgressChart
