import React, { useEffect, useState } from 'react';
import Cookies from 'js-cookie';
import {LineChart, Line, ResponsiveContainer, XAxis, YAxis, Tooltip, Legend} from 'recharts';

const ProgressChart = ({exerciseId}) => {

    const [stats, setStats] = useState([]);


    const fetchExerciseData = async (exerciseId) =>{
        try {
            const token = Cookies.get("accessToken");
            const userId = Cookies.get("userId");
            const response = await fetch(`http://localhost:3000/exercise/${exerciseId}/${userId}/workout-stats`, {
              method: "GET",
              headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${token}`,
              },
            });
            if (response.status === 200) {
              const result = await response.json();
              console.log(result);
              setStats(result);
            }
          } catch (err) {
            console.log(err);
          }
    }

    useEffect(()=>{
        fetchExerciseData();
    },[]);

  return (
    <ResponsiveContainer width={300} height={250}>
        <LineChart width={250} height={250} data={stats} >
            <XAxis  stroke='#fff'/>
            <YAxis stroke='#fff'/>
            <Tooltip />
            <Line type="monotone" dataKey='oneRM' stroke='#2563eb'/>
            <Line type="monotone" dataKey='volume' stroke='#7c3aed'/>
        </LineChart>
    </ResponsiveContainer>
  )
}

export default ProgressChart
