import React, { useEffect, useState } from 'react'
import SearchBar from './SearchBar';
import Cookies from 'js-cookie';
import './Panel.css'
import ProgressChart from './ProgressChart'

const ChartPanel = () => {
  const [results, setResults] = useState([]);
  const [search, setSearch] = useState(false);
  const [exerciseId, setExerciseId] = useState(3);
  const [exerciseInfo, setExercisesInfo] = useState([]);

  const fetchExerciseData = async (id) =>{
    try {
      const token = Cookies.get("accessToken");
      const response = await fetch(`http://localhost:3000/exercise/${id}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });
      if (response.status === 200) {
        const result = await response.json();
        console.log(result);
        setExercisesInfo(result);
      }
    } catch (err) {
      console.log(err);
    }
  };

  const handleSwitchingExercise = (exercise) =>{
    setExerciseId(exercise.id);
    setSearch(false);
  };

  useEffect(() =>{
    fetchExerciseData(exerciseId);
  },[exerciseId]);

  return (
    <div className='panel'>
        <div className='top'>
            <h1>Your Stats</h1>
        </div>
        <div className={search ? 'searchExercises' : 'searchExercises hidden'}>
          <SearchBar setResults={setResults} />
          <ul className='searchExercisesList'>
            {results.map((result, id) => (
              <li key={id} onClick={() => handleSwitchingExercise(result)}>
                {result.exercise}
              </li>
            ))}
          </ul>
        </div>
        <div className={search ? 'bottom hidden': 'bottom'}>
            <div className='container'>
                <h2>{exerciseInfo.exercise}</h2>
                <ProgressChart exerciseId={exerciseId}/>
                <button>Change the exercise</button>
            </div>
        </div>
    </div>
  )
}

export default ChartPanel
