import React, { useEffect, useState } from 'react'
import Cookies from 'js-cookie';
import './Panel.css'
import ProgressChart from './ProgressChart'

const ChartPanel = () => {
  const [search, setSearch] = useState(false);
  const [exerciseId, setExerciseId] = useState(3);

  const fetchExerciseData = async (id) =>{
    try {
      const token = Cookies.get("accessToken");
      const response = await fetch(`http://localhost:3000/exercise/${id}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });
      if (response.status === 200) {
        const result = await response.json();
        console.log(result);
        setExerciseId(result);
      }
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() =>{
    fetchExerciseData(exerciseId);
  },[exerciseId]);

  return (
    <div className='panel'>
        <div className='top'>
            <h1>Your Stats</h1>
        </div>
        <div className={search ? 'searchExercises' : 'searchExercises hidden'}>

        </div>
        <div className='bottom'>
            <div className='container'>
                <h2>Squat</h2>
                <ProgressChart exerciseId={169}/>
            </div>
            
        </div>
    </div>
  )
}

export default ChartPanel
