import React, { useEffect, useState } from 'react'
import Cookies from 'js-cookie';

const WorkoutView = ({id}) => {

    const [workoutName, setWorkoutName] = useState('');
    const [exercises, setExercises] = useState([]);

    const fetchWorkoutData = async () =>{
    try{
        const token = Cookies.get('accessToken');
        const response = await fetch(`http://localhost:3000/workout/${id}`,{
            method: "GET",
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`,
            },
      });
      if (response.status === 200){
        const result = await response.json();
        setWorkoutName(result.workout.name);
        setExercises(result.workout.exercises);
      }
    }
    catch(err){
        console.log(err);
    }
  };

  useEffect(()=>{
    fetchWorkoutData();
  },[])

  return (
    <div className='panel'>
      <h1>{workoutName}</h1>
      <div className='searchExercisesList'>
      {exercises.map((exercise, exerciseIndex) => (
            <li key={exerciseIndex} className='exercise'>
              <strong>{exercise.exercise}</strong>
              <table>
                <thead>
                  <tr>
                    <th>Set</th>
                    <th>KG</th>
                    <th>REP</th>
                    <th>RPE</th>
                  </tr>
                </thead>
                <tbody>
                  {exercise.sets.map((set, setIndex) => (
                    <tr key={setIndex}>
                      <td>{setIndex + 1}.</td>
                      <td>
                        {set.kg}
                      </td>
                      <td>
                        {set.reps}
                      </td>
                      <td>
                        {set.rpe}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </li>
          ))}
      </div>
    </div>
  )
}

export default WorkoutView
