import React, { useState } from 'react';
import './Panel.css';
import './WorkoutStart.css';
import SearchBar from './SearchBar';

const WorkoutStart = () => {
  const [search, setSearch] = useState(true);
  const [results, setResults] = useState([]);
  const [exercises, setExercises] = useState([]);

  const handleAddingExercise = (exercise) => {
    setExercises([
      ...exercises, 
      { exercise: exercise.exercise, sets: [{ kg: '', reps: '', rpe: '' }] }
    ]);
    setSearch(false);
  };

  const handleAddingSet = (exerciseIndex) => {
    const updatedExercises = [...exercises];
    updatedExercises[exerciseIndex].sets.push({ kg: '', reps: '', rpe: '' });
    setExercises(updatedExercises);
  };

  const handleInputChange = (exerciseIndex, setIndex, field, value) => {
    const updatedExercises = [...exercises];
    updatedExercises[exerciseIndex].sets[setIndex][field] = value;
    setExercises(updatedExercises);
    console.log(exercises);
  };

  return (
    <div className='panel'>
      <div className='top'>
        <h1>Workout</h1>
      </div>
      <div className={search ? 'bottomWorkout hidden' : 'bottomWorkout'}>
        <ul className='searchExercisesList'>
          {exercises.map((exercise, exerciseIndex) => (
            <li key={exerciseIndex} className='exercise'>
              <strong>{exercise.exercise}</strong>
              <ul>
                {exercise.sets.map((set, setIndex) => (
                  <li key={setIndex}>
                    KG <input 
                      value={set.kg} 
                      onChange={(e) => handleInputChange(exerciseIndex, setIndex, 'kg', e.target.value)}
                    /> 
                    REP <input 
                      value={set.reps} 
                      onChange={(e) => handleInputChange(exerciseIndex, setIndex, 'reps', e.target.value)}
                    /> 
                    RPE <select
                      value={set.rpe}
                      onChange={(e) => handleInputChange(exerciseIndex, setIndex, 'rpe', e.target.value)}
                    >
                      {[...Array(6)].map((_, i) => {
                        const value = i + 5; // Values from 5 to 10
                        return <option key={value} value={value}>{value}</option>;
                      })}
                    </select>
                  </li>
                ))}
                <button onClick={() => handleAddingSet(exerciseIndex)}>+</button>
              </ul>
            </li>
          ))}
        </ul>
        <button onClick={() => setSearch(!search)}>Add an exercise</button>
      </div>
      <div className={search ? 'searchExercises' : 'searchExercises hidden'}>
        <SearchBar setResults={setResults} />
        <ul className='searchExercisesList'>
          {results.map((result, id) => (
            <li key={id} onClick={() => handleAddingExercise(result)}>
              {result.exercise}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default WorkoutStart;
