import React, { useEffect, useState } from 'react';
import './Panel.css';
import ProgressChart from './ProgressChart';
import Cookies from 'js-cookie';

const Panel = () => {


  const [username,setUsername] = useState('');
  const [loading, setLoading] = useState(true);

  const fetchUserData = async () =>{
    try{
      const token = Cookies.get('accessToken');
      const response = await fetch('http://localhost:3000/userInfo',{
        method: "GET",
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
      });
      const result = await response.json();
      setUsername(result.user.username);
    } catch(err){
      console.error(err);
    } finally{
      setLoading(false);
    }
    

  }

  useEffect(()=>{
    fetchUserData();
  },[]);


  return (
    <div>
    {loading ? ( // Render loading state if loading is true
            <div className='loading-spinner' />
          ) : (
    
    <div className='panel'>
    

      <div className='top'>
        <div className='topLeft'>
            
            <div className='lastWorkout'>
            <h1>Hi {username} Your Last workouts</h1> 
                <ul>
                    <li>Monday's Workout, May 19 2024</li>
                    <li>Monday's Workout, May 19 2024</li>
                    <li>Monday's Workout, May 19 2024</li>
                </ul>
            </div>
        </div>
        <div className='topMiddle'>
            
            <div className='startWorkout'>
              <h1>Start new workout</h1>
              <button>Start</button> 
              
                
            </div>
        </div>
        <div className='topRight'>
            
            <div className='recentPrs'>
            <h1>Recent PR's</h1>
                <ul>
                    <li>Squat 200kgx3 RPE 8</li>
                    <li>Squat 200kgx3 RPE 9</li>
                    <li>Squat 200kgx3 RPE 10</li>
                </ul>
            </div>
        </div>
      </div>
      <div className='middle'>
        <div className='leftContainer'>
          <div className='chartContainer'>
            <h1>Squat Progress</h1>
            <ProgressChart />
          </div>
        </div>
        <div className='middleContainer'>
          <div className='chartContainer'>
            <h1>Bench Press Progress</h1>
            <ProgressChart />
          </div>
        </div>
        <div className='rightContainer'>
          <div className='chartContainer'>
            <h1>Deadlift Progress</h1>
            <ProgressChart />
          </div>
        </div>
      </div>
      </div>)}
      </div>

  )
}

export default Panel
